$(function(){$('a[href^=#]').click(function(){var speed=500;var href=$(this).attr("href");var target=$(href=="#"||href==""?'html':href);var position=target.offset().top;$("html, body").animate({scrollTop:position},speed,"swing");return false;});});$(function(){$(window).bind("scroll",function(){if($(this).scrollTop()>150){$(".pageTop").fadeIn();}else{$(".pageTop").fadeOut();}scrollHeight=$(".main").height()+20;scrollPosition=$(window).height()+$(window).scrollTop();footHeight=$(".footer").height();windowWidth=$(".main").width();if(windowWidth<1080){$(".pageTop a").css({"position":"fixed","bottom":"5px"});}else{if(scrollHeight<=scrollPosition){$(".pageTop a").css({"position":"absolute","bottom":"40px"});}else{$(".pageTop a").css({"position":"fixed","bottom":"0px"});}}});});